const Vinrainbow=function(t){this.selector=t,this.factor=[],this.por=this.selector.clientHeight/this.selector.clientWidth,this.frame=this.selector.clientHeight+this.selector.clientWidth,this.init()};Vinrainbow.prototype={init:function(){this.calculate()},calculate:function(){function t(t,e){return Math.random()*(e-t)+t}for(var e=this,r=0;r<6;r++){var i=Math.floor(t(800,2e3));this.factor.push(i)}this.selector.onmousemove=function(){var t=event.clientY+10*event.clientX,r=parseInt((t+e.factor[0])/(e.frame+e.factor[1])*60),i=parseInt((t+e.factor[2])/(e.frame+e.factor[3])*60),o=parseInt((t+e.factor[4])/(e.frame+e.factor[5])*60);e.selector.style.backgroundColor="rgb("+r+","+i+","+o+")"}}};var rainbow=document.querySelector("#sidebar");new Vinrainbow(rainbow);