const Vinrainbow=function(t){this.selector=t,this.factor=[],this.por=this.selector.clientHeight/this.selector.clientWidth,this.frame=this.selector.clientHeight+this.selector.clientWidth,this.init()};Vinrainbow.prototype={init:function(){this.mouseEvent()},mouseEvent:function(){function t(t,e){return Math.random()*(e-t)+t}for(var e=this,o=0;o<6;o++){var n=Math.floor(t(800,2e3));this.factor.push(n)}this.selector.onmousemove=function(){var t=event.clientY+10*event.clientX,o=parseInt((t+e.factor[0])/(e.frame+e.factor[1])*60),n=parseInt((t+e.factor[2])/(e.frame+e.factor[3])*60),r=parseInt((t+e.factor[4])/(e.frame+e.factor[5])*60);e.selector.style.backgroundColor="rgb("+o+","+n+","+r+")"}}},function(){var t=window.location.href,e=t.split("?")[1],o=document.querySelectorAll(".zh-cn"),n=document.querySelectorAll(".en-us");if("zh-cn"==e)for(var r=0;r<o.length;r++)o[r].style.display="block",n[r].style.display="none";else for(var r=0;r<o.length;r++)o[r].style.display="none",n[r].style.display="block"}();var rainbow=document.querySelector("#sidebar");new Vinrainbow(rainbow);